package ex_07_Increment_decrement;

public class Lab0076_Task4_13June {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		 int x = 5;

	        System.out.println(++x); // Output: 6

	        System.out.println(x++); // Output: 6

	        System.out.println(x);   // Output: 7
	}

}
