package ex_09_loop;

public class Lab0086_For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//For loop
		// Help you to repeat a block of code
		// Initialization -> condition -> Updation(Increase/decrease)
		// ICU
		
		for(int i=0; i < 10; i++) //runs 10 times
		{
			System.out.println(i); // 0 to 9
		}
		
	}

}
