package ex_09_loop;

public class Lab_0090_For_if_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int shruti = 0; shruti <18; shruti++)
		{
			// 0 to 17, loop will run 18 times
			if(shruti > 15)
			{
				System.out.println("She will get iphone");
			}
			else
			{
				System.out.println("She will only get candy!!");
			}
		}
	}

}
